    .message_mode           = ${module.driver.can.message_mode} ,
    .p_callback             = ${module.driver.can.p_callback},
    .p_extend               = &${module.driver.can.name}_extended_cfg,
    .p_context              = &${module.driver.can.name},
    .error_ipl              = ${module.driver.can.error_ipl},
    .mailbox_tx_ipl         = ${module.driver.can.mailbox_tx_ipl},
    .mailbox_rx_ipl         = ${module.driver.can.mailbox_rx_ipl},
};
/* Instance structure to use this module. */
const can_instance_t ${module.driver.can.name} = 
{
